arguments.argument:
maxHp = 120;
runPower = 1;
weapons[0] = Minegun;
haxxyStatue = DemomanHaxxyStatueS;
tauntsprite = DemoTauntS;

numFlames = 3;

// 'GW' reward - *G*olden *W*eapon
if(hasClassReward(global.paramPlayer, 'GW'))
{
    // Prepend to the start of the overlay list, so that it gets drawn first (it's a full sprite replacement)
    ds_list_insert(tauntOverlays, 0, DemoGoldWeaponTauntS);
}

if (player.team == TEAM_RED)
{
    sprite_index = DemomanRedS;
    humiliationPoses = DemomanRedHS;
    spriteRun = DemomanRedRunS;
    spriteJump = DemomanRedJumpS;
    spriteStand = DemomanRedStandS;
    spriteLeanR = DemomanRedLeanRS;
    spriteLeanL = DemomanRedLeanLS;
    spriteIntel = DemomanRedIntelS;
}
else if (player.team == TEAM_BLUE)
{
    sprite_index = DemomanBlueS;
    humiliationPoses = DemomanBlueHS;
    spriteRun = DemomanBlueRunS;
    spriteJump = DemomanBlueJumpS;
    spriteStand = DemomanBlueStandS;
    spriteLeanR = DemomanBlueLeanRS;
    spriteLeanL = DemomanBlueLeanLS;
    spriteIntel = DemomanBlueIntelS;
}
