arguments.argument:
if global.dsmFPSDisplay!=1 exit;

if fps==30/global.delta_factor{
    fpsColour=c_green
}else if fps<30/global.delta_factor{
    fpsColour=c_yellow
}else if fps<20/global.delta_factor{
    fpsColour=c_red
}

draw_set_valign(fa_center)
draw_set_halign(fa_middle)
draw_set_color(c_black)
draw_set_alpha(0.6)
draw_rectangle(view_xview+100,view_yview+30,view_xview+140,view_yview+60,false)

draw_set_color(fpsColour)
draw_set_alpha(0.6)
draw_text(view_xview+120,view_yview+45,fps)
draw_set_color(c_white)
