arguments.argument:
if(killedby.object != -1){
    hp=killedby.object.hp;
    maxHp=killedby.object.maxHp;
    killMessage="Your killer had "+string(hp)+"/"+string(maxHp)+"HP."
}else if(lastDamageSource == DAMAGE_SOURCE_SENTRYTURRET and killedby.sentry){
    hp=killedby.sentry.hp;
    maxHp=killedby.sentry.maxHp;
    killMessage="Your killer had "+string(hp)+"/"+string(maxHp)+"HP."
}else{
    //yourself or they died before you
    hp=0
    maxHp=0
    killMessage="Your killer is dead."
}
