arguments.argument:
{
    menu_create(48, 172, 500, 260, 32, 40, 104, 6);
    menumode = true;

    if (room != DSMOptionsR)
        menu_setdimmed();

    menu_background(512, 24, 8, 12, 4);
    bgtabs = true;

    menu_addedit_select("HP Text:","global.dsmHPText",'
        DSM_write_ini("Cosmetic","DSMHPText",argument0)
    ')
    menu_add_option(0,"Off")
    menu_add_option(1,"Centre")
    menu_add_option(2,"Left")

    menu_addedit_boolean("Ammo Bar:","global.dsmAmmoBars",'
        DSM_write_ini("Cosmetic","DSMAmmoBars",argument0)
    ')
    menu_addedit_boolean("Show Scoreboard When Dead:","global.dsmShowScoreboardWhenDead",'
        DSM_write_ini("Settings","DSMShowScoreboardWhenDead",argument0)
    ')
    menu_addedit_boolean("Show Assists:","global.dsmShowAssists",'
        DSM_write_ini("Settings","DSMShowAssists",argument0)
    ')
    menu_addedit_boolean("Killer Info:","global.dsmKillerInfo",'
        DSM_write_ini("Settings","DSMKillerInfo",argument0)
    ')
    menu_addedit_boolean("Record Stats:","global.dsmRecordStats",'
        DSM_write_ini("Settings","DSMRecordStats",argument0)
    ')
    menu_addedit_boolean("Always Show Spinjump Particles:","global.dsmAlwaysShowSpinjump",'
        DSM_write_ini("Cosmetic","DSMAlwaysShowSpinjump",argument0)
    ')
    menu_addedit_boolean("Use Old Blood:","global.dsmOldBlood",'
        DSM_write_ini("Cosmetic","DSMOldBlood",argument0)
    ')
    menu_addedit_num("Sound Volume:","global.dsmVolume",'
        DSM_write_ini("Settings","DSMVolume",argument0)
        sound_global_volume(argument0/100)
    ',100)
    menu_addedit_num("HUD Opacity:","global.dsmHudOpacity",'
        DSM_write_ini("Cosmetic","DSMHUDOpacity",argument0)
        global.dsmHudOpacityReal=argument0/100
    ',100)
    menu_addedit_select("Body Fade Speed:","global.dsmBodyFade",'
        DSM_write_ini("Cosmetic","DSMBodyFade",argument0)
    ')
    menu_add_option(0,"Normal")
    menu_add_option(1,"Fast")
    menu_add_option(2,"Very Fast")

    menu_addedit_boolean("Show Plugin List:","global.dsmShowPluginList",'
        DSM_write_ini("Settings","DSMShowPluginList",argument0)
    ')

    menu_addtablink("DSM 1", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController);
    ');
    menu_addtablink("DSM 2", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController2);
    ');
    menu_addtablink("DSM 3", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController3);
    ');
    menu_addtablink("DSM 4", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController4);
    ');
    menu_addtablink("Controls", '
        instance_destroy();
        instance_create(x,y,DSMControlsController);
    ');

    menu_addback("Back", '
        instance_destroy();
        if(room == DSMOptionsR)
            room_goto_fix(Menu);
        else
            instance_create(0,0,InGameMenuController);
    ');
}
