arguments.argument:
{
    menu_create(48, 172, 500, 260, 32, 40, 104, 6);
    menumode = true;

    if (room != DSMOptionsR)
        menu_setdimmed();

    menu_background(512, 24, 8, 12, 4);
    bgtabs = true;

    menu_addedit_num("DSM Chat Box Transparency:","global.dsmChatBoxAlpha",'
        DSM_write_ini("Cosmetic","DSMChatBoxAlpha",argument0)
    ',100)

    menu_addedit_num("DSM Chat Text Transparency:","global.dsmChatTextAlpha",'
        DSM_write_ini("Cosmetic","DSMChatTextAlpha",argument0)
    ',100)

    menu_addedit_boolean("Always Record Replay:","global.dsmAlwaysRecordReplay",'
        DSM_write_ini("Settings","DSMAlwaysRecordReplay",argument0)
    ')

    //menu_addedit_boolean("Split Replays By Map:","global.dsmSplitReplaysByMap",'
    //    DSM_write_ini("Settings","DSMSplitReplaysByMap",argument0)
    //')

    menu_addedit_boolean("Write Chat Log To File:","global.dsmWriteChatLog",'
        DSM_write_ini("Settings","DSMWriteChatLog",argument0)
    ')

    menu_addedit_select("Ping Graph:","global.dsmPingGraph",'
        DSM_write_ini("Settings","DSMPingGraph",argument0)
    ')
    menu_add_option(0,"Off")
    menu_add_option(1,"On")
    menu_add_option(2,"Show With Scoreboard")

    menu_addedit_select("Ping Frequency:","global.dsmPingFrequency",'
        DSM_write_ini("Settings","DSMPingFrequency",argument0)
    ')
    menu_add_option(0,"Very Fast")
    menu_add_option(1,"Fast")
    menu_add_option(2,"Medium")
    menu_add_option(3,"Slow")
    menu_add_option(4,"Very Slow")

    menu_addedit_select("Damage Indicator Colour:","global.damageColour",'
        DSM_write_ini("Cosmetic","DamageColour",argument0)
    ')
    menu_add_option(0,"Red")
    menu_add_option(1,"Yellow")
    menu_add_option(2,"Green")
    menu_add_option(3,"Blue")
    menu_add_option(4,"Pink")

    menu_addedit_select("Medic Targeting:","global.medicTargeting",'
        DSM_write_ini("Settings","MedicTargeting",argument0)
    ')
    menu_add_option(0,"Cursor Positon")
    menu_add_option(1,"Line Of Sight (Old)")

    menu_addtablink("DSM 1", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController);
    ');
    menu_addtablink("DSM 2", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController2);
    ');
    menu_addtablink("DSM 3", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController3);
    ');
    menu_addtablink("DSM 4", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController4);
    ');
    menu_addtablink("Controls", '
        instance_destroy();
        instance_create(x,y,DSMControlsController);
    ');

    menu_addback("Back", '
        instance_destroy();
        if(room == DSMOptionsR)
            room_goto_fix(Menu);
        else
            instance_create(0,0,InGameMenuController);
    ');
}
