arguments.argument:
var scoreKeyDown;
scoreKeyDown = keyboard_check_direct(global.showScores);
if(scoreKeyDown and !oldScoreKeyDown)
    inputShowScores();
else if(!scoreKeyDown and oldScoreKeyDown)
    inputCloseScores();
oldScoreKeyDown = scoreKeyDown;

if global.myself.team!=TEAM_SPECTATOR{
    if (global.dsmShowScoreboardWhenDead==1 and instance_exists(Spectator)){
        inputShowScores()
    }
    if (global.dsmShowScoreboardWhenDead==1 and !instance_exists(Spectator) and (!scoreKeyDown and oldScoreKeyDown)){
        inputCloseScores()
    }
}
