arguments.argument:
if global.drawHPHUD==0 exit;

var xoffset, yoffset, xsize, ysize;
xoffset = view_xview[0]
yoffset = view_yview[0]
xsize = view_wview[0]
ysize = view_hview[0]

if(global.myself.object == -1) {
    instance_destroy();
    exit; 
}

if global.bigHpNumbers==1{
    var hp, maxHp, scale;
    _hp = global.myself.object.hp
    _maxHp = global.myself.object.maxHp
    scale=3
    draw_set_valign(fa_center)
    draw_set_halign(fa_center)
    hpText = string(ceil(min(max(_hp,0),_maxHp)))

    col_custom=make_color_rgb(global.hpColourR,global.hpColourG,global.hpColourB)
    col_default=make_color_rgb(244,244,11)

    draw_text_transformed_color(xoffset+150+2, yoffset+ysize-100+2, hpText, scale, scale, 0, c_black, c_black, c_black, c_black, 1)
    draw_text_transformed_color(xoffset+150, yoffset+ysize-100, hpText, scale, scale, 0, col_custom, col_custom, col_custom, col_custom, 1)
}else if global.boxHUD==0{
    drawHealth(45, ysize-53, global.myself.object.hp, global.myself.object.maxHp);
    draw_sprite_ext(sprite_index, image_index, view_xview[0]+5, view_yview[0]+ysize-75, 2, 2, 0, c_white, 1);
}else if global.boxHUD==1{
    drawBoxHealth(23, ysize-37, global.myself.object.hp, global.myself.object.maxHp)
}
